(2, 1, '2020-06-02 11:20:17', '2020-06-02 11:20:17', '<!-- wp:group {\"align\":\"full\",\"className\":\"headline\"} -->\n<div class=\"wp-block-group alignfull headline\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"align\":\"full\",\"id\":122,\"sizeSlug\":\"large\",\"className\":\"banner-desktop\"} -->\n<figure class=\"wp-block-image alignfull size-large banner-desktop\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/bgTamanv2-1024x533.png\" alt=\"\" class=\"wp-image-122\" title=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"align\":\"full\",\"id\":284,\"sizeSlug\":\"large\",\"className\":\"banner-mobile\"} -->\n<figure class=\"wp-block-image alignfull size-large banner-mobile\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/bgTamanMobile-1.png\" alt=\"\" class=\"wp-image-284\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"cloud-parallax\"} -->\n<div class=\"wp-block-group alignfull cloud-parallax\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":124,\"sizeSlug\":\"large\",\"className\":\"cloud1 alignfull\"} -->\n<figure class=\"wp-block-image size-large cloud1 alignfull\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/cloud1.png\" alt=\"\" class=\"wp-image-124\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":125,\"sizeSlug\":\"large\",\"className\":\"cloud2 alignfull\"} -->\n<figure class=\"wp-block-image size-large cloud2 alignfull\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/cloud2.png\" alt=\"\" class=\"wp-image-125\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":126,\"sizeSlug\":\"large\",\"className\":\"cloud3 alignfull\"} -->\n<figure class=\"wp-block-image size-large cloud3 alignfull\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/cloud3.png\" alt=\"\" class=\"wp-image-126\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":125,\"sizeSlug\":\"large\",\"className\":\"cloud4 alignfull\"} -->\n<figure class=\"wp-block-image size-large cloud4 alignfull\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/cloud2.png\" alt=\"\" class=\"wp-image-125\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":201,\"sizeSlug\":\"large\",\"className\":\"bush alignfull\"} -->\n<figure class=\"wp-block-image size-large bush alignfull\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/rumput2.png\" alt=\"\" class=\"wp-image-201\"/></figure>\n<!-- /wp:image --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"line-headline\"} -->\n<div class=\"wp-block-group alignfull line-headline\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":130,\"sizeSlug\":\"large\",\"className\":\"logo-headline\"} -->\n<figure class=\"wp-block-image size-large logo-headline\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/logoKK.png\" alt=\"\" class=\"wp-image-130\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#7650a1\",\"className\":\"text-headline the-minion\"} -->\n<p style=\"color:#7650a1\" class=\"has-text-color text-headline the-minion\">Fun Coding</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#7650a1\",\"className\":\"text-headline the-minion\"} -->\n<p style=\"color:#7650a1\" class=\"has-text-color text-headline the-minion\">Course for Kids</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons {\"className\":\"button-headline\"} -->\n<div class=\"wp-block-buttons button-headline\"><!-- wp:button {\"customBackgroundColor\":\"#64bc46\",\"borderRadius\":34} -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link has-background\" href=\"http://localhost:8080/kodekiddo2/get-started/\" style=\"background-color:#64bc46;border-radius:34px\">Get Started</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {\"align\":\"full\",\"className\":\"bg-donuttop\"} -->\n<div class=\"wp-block-columns alignfull bg-donuttop\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"customTextColor\":\"#327ec0\",\"className\":\"the-minion p-heading\"} -->\n<p style=\"color:#327ec0\" class=\"has-text-color the-minion p-heading\">Learn to Code With <strong>Kodekiddo</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[136],\"align\":\"full\",\"className\":\"bb-8 bb-8-mobile\"} -->\n<figure class=\"wp-block-gallery alignfull columns-1 is-cropped bb-8 bb-8-mobile\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/bb8both.png\" alt=\"\" data-id=\"136\" data-full-url=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/bb8both.png\" data-link=\"http://localhost:8080/kodekiddo2/sample-page/bb8both/\" class=\"wp-image-136\"/></figure></li></ul></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:paragraph {\"className\":\"line-height p-body\"} -->\n<p class=\"line-height p-body\">KodeKiddo adalah tempat <strong>belajar coding</strong> yang interaktif dan seru untuk anak-anak.&nbsp;Kami menawarkan&nbsp; kelas untuk <strong>kegiatan after school dan aktivitas untuk liburan</strong> sekolah&nbsp;di lokasi KodeKiddo atau di sekolah, dengan menggunakan <strong>teknologi </strong>dan <strong>metodologi </strong>yang dikembangkan <strong>khusus untuk anak.</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"className\":\"p-body\"} -->\n<p class=\"p-body\">Dengan kantor pusat di Silicon Valley USA, KodeKiddo membawa program-program global yang dilakukan di negara maju dan kesempatan partisipasi di kompetisi coding internasional.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons {\"className\":\"button-joinus\"} -->\n<div class=\"wp-block-buttons button-joinus\"><!-- wp:button {\"textColor\":\"background\",\"customBackgroundColor\":\"#64bc46\",\"borderRadius\":34,\"className\":\"is-style-fill\"} -->\n<div class=\"wp-block-button is-style-fill\"><a class=\"wp-block-button__link has-text-color has-background-color has-background\" href=\"http://localhost:8080/kodekiddo2/register-contact/\" style=\"background-color:#64bc46;border-radius:34px\" target=\"_blank\" rel=\"noreferrer noopener\">Join Us</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:gallery {\"ids\":[136],\"align\":\"full\",\"className\":\"bb-8 bb-8-desktop\"} -->\n<figure class=\"wp-block-gallery alignfull columns-1 is-cropped bb-8 bb-8-desktop\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/bb8both.png\" alt=\"\" data-id=\"136\" data-full-url=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/bb8both.png\" data-link=\"http://localhost:8080/kodekiddo2/sample-page/bb8both/\" class=\"wp-image-136\"/></figure></li></ul></figure>\n<!-- /wp:gallery --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"full\",\"className\":\"channel\"} -->\n<div class=\"wp-block-columns alignfull channel\"><!-- wp:column {\"className\":\"kk-channel-1\"} -->\n<div class=\"wp-block-column kk-channel-1\"><!-- wp:group {\"align\":\"full\",\"className\":\"channel-1\"} -->\n<div class=\"wp-block-group alignfull channel-1\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"align\":\"full\",\"id\":143,\"sizeSlug\":\"large\",\"className\":\"img-program-sd1\"} -->\n<figure class=\"wp-block-image alignfull size-large img-program-sd1\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/stay_at_home_biohazard_warning-wallpaper-2400x1350-1-1024x576.jpg\" alt=\"\" class=\"wp-image-143\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"channel-text\"} -->\n<div class=\"wp-block-group alignfull channel-text\"><div class=\"wp-block-group__inner-container\"><!-- wp:list {\"className\":\"head-channel txt-color p-body\"} -->\n<ul class=\"head-channel txt-color p-body\"><li><strong>EVENT HOLIDAY PROGRAM KODEKIDDO</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"body-channel\"} -->\n<div class=\"wp-block-group alignfull body-channel\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"textColor\":\"background\",\"className\":\"p-body\"} -->\n<p class=\"has-text-color has-background-color p-body\">Program populer ini kembali lagi untuk mengisi waktu luang kiddos di saat liburan sekolah 2020!!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<p style=\"color:#ffffff\" class=\"has-text-color has-small-font-size left-margin\"><strong><a href=\"http://localhost:8080/kodekiddo2/holiday-program/\">VIEW MORE</a></strong> <i class=\"fas fa-arrow-right\"></i></p>\n<!-- /wp:html --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"channel-3\"} -->\n<div class=\"wp-block-group alignfull channel-3\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"align\":\"full\",\"id\":143,\"sizeSlug\":\"large\",\"className\":\"img-program-sd1\"} -->\n<figure class=\"wp-block-image alignfull size-large img-program-sd1\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/stay_at_home_biohazard_warning-wallpaper-2400x1350-1-1024x576.jpg\" alt=\"\" class=\"wp-image-143\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"channel-text\"} -->\n<div class=\"wp-block-group alignfull channel-text\"><div class=\"wp-block-group__inner-container\"><!-- wp:list {\"className\":\"head-channel txt-color p-body\"} -->\n<ul class=\"head-channel txt-color p-body\"><li><strong>KODEKIDDO ONLINE CLASS</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"body-channel\"} -->\n<div class=\"wp-block-group alignfull body-channel\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"textColor\":\"background\",\"className\":\"p-body\"} -->\n<p class=\"has-text-color has-background-color p-body\">Untuk tetap mendukung proses belajar kiddos di saat periode sekolah dan kelas ditutup karena Covid-19, KodeKiddo menyediakan beberapa program coding yang bisa dilakukan secara online.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<p style=\"color:#ffffff\" class=\"has-text-color has-small-font-size left-margin\"><strong><a href=\"http://localhost:8080/kodekiddo2/online-class/\">VIEW MORE</a></strong> <i class=\"fas fa-arrow-right\"></i></p>\n<!-- /wp:html --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"className\":\"kk-channel bg-purple\"} -->\n<div class=\"wp-block-column kk-channel bg-purple\"><!-- wp:group {\"align\":\"full\",\"className\":\"channel-2 bg-purple\"} -->\n<div class=\"wp-block-group alignfull channel-2 bg-purple\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"textColor\":\"background\",\"className\":\"the-minion top-margin p-heading\"} -->\n<p class=\"has-text-color has-background-color the-minion top-margin p-heading\"><strong>KodeKiddo </strong>Channel</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"background\",\"className\":\"p-body\"} -->\n<p class=\"has-text-color has-background-color p-body\">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed vitae tempor odio, in tincidunt odio. Donec at ligula porta, sagittis </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"className\":\"top-margin txt-color p-body\"} -->\n<ul class=\"top-margin txt-color p-body\"><li><strong>KODEKID