(413, 1, '2020-06-07 13:06:52', '2020-06-07 13:06:52', '<!-- wp:group {\"align\":\"full\",\"className\":\"pages\"} -->\n<div class=\"wp-block-group alignfull pages\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"customTextColor\":\"#64bc46\",\"className\":\"alignwide the-minion p-heading header-title\"} -->\n<p style=\"color:#64bc46\" class=\"has-text-color has-text-align-center alignwide the-minion p-heading header-title\">HOLIDAY PROGRAM – ONLINE CODING CAMP</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group {\"align\":\"full\",\"className\":\"pages-body\"} -->\n<div class=\"wp-block-group alignfull pages-body\"><div class=\"wp-block-group__inner-container\"><!-- wp:image {\"id\":46,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost:8080/kodekiddo2/wp-content/uploads/2020/06/picsart_04-13-04.04.46-1024x1024.png\" alt=\"\" class=\"wp-image-46\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":6,\"className\":\"p-body alignfull\"} -->\n<h6 class=\"p-body alignfull\">PROGRAM POPULER INI KEMBALI LAGI UNTUK MENGISI WAKTU KIDDOS DI SAAT LIBURAN SEKOLAH 2020!!</h6>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":6,\"className\":\"p-body alignfull\"} -->\n<h6 class=\"p-body alignfull\">LEBIH BANYAK TOPIK BARU…. LEBIH BANYAK LOKASI….</h6>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":6,\"className\":\"p-body alignfull\"} -->\n<h6 class=\"p-body alignfull\">KALI INI KELAS DILAKUKAN SECARA ONLINE, KIDDOS IKUT KELAS DARI RUMAH MASING-MASING.</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"className\":\"p-body alignfull\"} -->\n<p class=\"p-body alignfull\">Join KodeKiddo coding holiday program.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"className\":\"p-body alignfull\"} -->\n<ul class=\"p-body alignfull\"><li>Kelas dilakukan online dengan platform Webex atau Zoom</li><li>Didampingi full time oleh instruktur KodeKiddo yang berpengalaman</li><li>Program untuk kidd